// Handle input from Telegram or Webhook
const input = $input.first().json;
let imageData = null;
let userInfo = {};
let source = '';

// Check if input is from Telegram
if (input.message && input.message.photo) {
  source = 'telegram';
  userInfo = {
    userId: input.message.from.id,
    username: input.message.from.username || input.message.from.first_name,
    chatId: input.message.chat.id
  };
  // Get the highest quality photo
  const photos = input.message.photo;
  const highestQuality = photos[photos.length - 1];
  imageData = {
    fileId: highestQuality.file_id,
    type: 'telegram_file_id'
  };
} else if (input.imageUrl || input.imageData) {
  source = 'webhook';
  userInfo = {
    userId: input.userId || 'web_user',
    username: input.username || 'Web User'
  };
  imageData = {
    url: input.imageUrl,
    data: input.imageData,
    type: input.imageUrl ? 'url' : 'base64'
  };
} else {
  throw new Error('No image data found in input');
}

// Generate unique workflow ID
const workflowId = 'wine-social-' + Date.now() + '-' + Math.random().toString(36).substr(2, 9);

return {
  workflowId,
  source,
  userInfo,
  imageData,
  timestamp: new Date().toISOString()
};

// Parse AI analysis and create social media content
const analysis = $input.first().json.choices[0].message.content;
const workflowData = $('Input Processor').first().json;

// Extract content from analysis
const lines = analysis.split('\n');
let wineType = 'Premium Wine';
let brandPositioning = 'Luxury';
let targetAudience = 'Wine Enthusiasts';
let instagramCaption = '';
let tiktokScript = '';
let facebookPost = '';
let twitterPost = '';
let videoStyle = 'Cinematic';
let videoDuration = '30';
let callToAction = 'Shop now!';

// Parse analysis (simplified extraction)
let currentSection = '';
lines.forEach(line => {
  const trimmed = line.trim();
  if (trimmed.includes('Wine type:')) wineType = trimmed.split(':')[1]?.trim() || wineType;
  if (trimmed.includes('Brand positioning:')) brandPositioning = trimmed.split(':')[1]?.trim() || brandPositioning;
  if (trimmed.includes('Target audience:')) targetAudience = trimmed.split(':')[1]?.trim() || targetAudience;
  if (trimmed.includes('Instagram caption:')) currentSection = 'instagram';
  if (trimmed.includes('TikTok script:')) currentSection = 'tiktok';
  if (trimmed.includes('Facebook post:')) currentSection = 'facebook';
  if (trimmed.includes('Twitter post:')) currentSection = 'twitter';
  if (trimmed.includes('Video style:')) videoStyle = trimmed.split(':')[1]?.trim() || videoStyle;
  if (trimmed.includes('Duration:')) videoDuration = trimmed.split(':')[1]?.trim().replace('seconds', '').trim() || videoDuration;
  if (trimmed.includes('Call-to-action:')) callToAction = trimmed.split(':')[1]?.trim() || callToAction;

  // Collect content based on current section
  if (currentSection === 'instagram' && trimmed && !trimmed.includes('Instagram caption:')) {
    instagramCaption += trimmed + '\n';
  } else if (currentSection === 'tiktok' && trimmed && !trimmed.includes('TikTok script:')) {
    tiktokScript += trimmed + '\n';
  } else if (currentSection === 'facebook' && trimmed && !trimmed.includes('Facebook post:')) {
    facebookPost += trimmed + '\n';
  } else if (currentSection === 'twitter' && trimmed && !trimmed.includes('Twitter post:')) {
    twitterPost += trimmed + '\n';
  }
});

// Create video generation prompt
const videoPrompt = `Create a professional wine bottle marketing video with the following specifications:

Wine Type: ${wineType}
Brand Positioning: ${brandPositioning}
Target Audience: ${targetAudience}
Video Style: ${videoStyle}
Duration: ${videoDuration} seconds
Call to Action: ${callToAction}

Include:
- Smooth bottle rotation
- Elegant wine pour animation
- Professional lighting
- Brand label focus
- Sophisticated background
- Cinematic camera movements
- Text overlay with key selling points

Style: High-end, luxurious, professional wine advertisement for social media`;

return {
  workflowId: workflowData.workflowId,
  source: workflowData.source,
  userInfo: workflowData.userInfo,
  wineType,
  brandPositioning,
  targetAudience,
  instagramCaption: instagramCaption.trim(),
  tiktokScript: tiktokScript.trim(),
  facebookPost: facebookPost.trim(),
  twitterPost: twitterPost.trim(),
  videoStyle,
  videoDuration: parseInt(videoDuration),
  callToAction,
  videoPrompt,
  timestamp: new Date().toISOString()
};

// Process video generation result
const videoResult = $input.first().json;
const contentData = $('Content Generator').first().json;

let videoUrl = null;
if (videoResult && videoResult.output) {
  videoUrl = videoResult.output;
}

return {
  ...contentData,
  videoUrl,
  videoStatus: videoUrl ? 'generated' : 'failed',
  timestamp: new Date().toISOString()
};
